div.style:
position: relative;
width:100%;
height:100%;
border-radius:50%;
overflow: hidden;

width:100%;
height:100%;
border-radius:50%;
animation: spin 4s linear infinite;
background:
  repeating-radial-gradient(circle at 50% 50%,
    rgba(255,255,255,0.05) 0px,
    rgba(0,0,0,0.15) 6px,
    rgba(0,0,0,0) 12px,
    rgba(0,0,0,0) 24px
  ),
conic-gradient(
from 90deg,
#222 0deg,
#444 60deg,
#2d2d2d 120deg,
#4a4a4a 180deg,
#3c3c3c 240deg,
#333 300deg,
#222 360deg
);

  position:absolute;
  top:0;
  left:0;
  width:100%;
  height:100%;
  border-radius:50%;
  background:conic-gradient(
    from 90deg,
    rgba(0,0,0,0.5) 0deg,
    rgba(0,0,0,0) 60deg,
    rgba(151, 151, 151, 0.5) 120deg,
    rgba(0,0,0,0) 180deg,
    rgba(0,0,0,0.5) 240deg,
    rgba(0,0,0,0) 300deg,
    rgba(0,0,0,0.5) 360deg
);

  pointer-events:none;
